      } else {
        indicator.style.top = `${height * 0}%`;
      }
    },
    logout() {
      document.getElementById('postionInNavigation').style.top = `${8.3 * 8}%`;
      window.location.href = `${this.$urlHttp}/logout`;
      this.fullscreenLoading = true;
    }
  }
};
</script>

